INSERT INTO `task` VALUES (1, '搬家清空', '非退房清洁，需要代理清空不要的个人物品，非厨余垃圾，只需要代扔。', 50.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-04-01 19:29:43', 2);
INSERT INTO `task` VALUES (2, '带我熟悉校园', '我是刚来到澳洲的留学生，希望学长学姐能带我熟悉一下校园环境和周边。', 5.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-03-26 19:29:52', 0);
INSERT INTO `task` VALUES (3, '需要一个专车导游带我爸妈玩三天', '我爸妈退休了，想出去旅游，但是我忙着考试，没办法抽空，希望能找个专业的导游，要包车哦！', 500.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-03-18 19:30:26', 1);
INSERT INTO `task` VALUES (4, '清洁我的房间', '房间不脏主要是有点乱。自己懒得收拾，帮我把东西摆放整齐了就行。', 30.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-03-20 19:30:43', 3);
INSERT INTO `task` VALUES (5, '帮我打游戏', '游戏是魔兽争霸，主要是日常，最近忙着准备考试，没有时间，主角色至少60级以上。', 100.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-04-17 19:30:51', 1);
INSERT INTO `task` VALUES (6, '找个家庭教师', '找数学老师，孩子上初中，基础还行，想要进一步提升，需要有相关教师资格认证或者本地名校毕业。', 5000.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-05-07 19:31:00', 2);
INSERT INTO `task` VALUES (7, '帮我照看宠物', '自己准备回老家看望一下父母，宠物暂时无人照看。要求：每天早上7：00，中午12：30，晚上8：30准时偷食。', 200.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-04-23 19:31:12', 1);
INSERT INTO `task` VALUES (8, '后台管理系统', '零售行业后台管理系统：1. 订单管理 2. 报表管理 3. 会员管理模块和系统设置部分页面bug修改。4. 微信公众号分享功能bug\r\n\r\n后期还有二期、三期需求，希望长期合作！', 10000.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-03-31 20:29:47', 2);
INSERT INTO `task` VALUES (9, '文本分类模型设计与试验分析', '1、手上有少量数据（部分已标记类别），需要设计一个具有创新性的文本分类模型，通过已分类数据训练模型，用训练好的模型去预测未分类的数据。\r\n需要模型设计的详细信息，以及实验结果分析。\r\n2、主要需求自然语言处理、机器学习方面的工程师。', 5000.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-02-01 20:40:08', 3);
INSERT INTO `task` VALUES (10, '分析用长周期数据库', '长周期大数据分析用的数据库：建立一个数据库自动读取按天保存的xls文件,（800个数据标签，秒级精度）能够具备条件筛选功能。如数据选定数据标签a的范围值，滤去范围外其他所有的标签相关数据，再在结果中设定标签b的，滤去其他。', 10000.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-02-18 21:28:40', 1);
INSERT INTO `task` VALUES (11, '工业UI设计', '按图进行UI设计，要求技术高超，有良好的审美，有工业或者监控中心驾驶舱设计经验尤佳。具体要求参看附图', 5000.0, '山东省聊城市莘县柿子园乡雷庄小学', '2021-03-31 15:52:20', 2);

